import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  socket: null,
};

export const socketSlice = createSlice({
  name: "socket",
  initialState,
  reducers: {
    init: (state, action) => {
      state.socket = action.payload;
    },
  },
});

export const { init } = socketSlice.actions;

export default socketSlice.reducer;
