import Phaser from "phaser";

class BootScene extends Phaser.Scene {
  constructor() {
    super("Boot");
  }

  preload() {
    this.loadImages();
    this.loadAudio();
    this.loadSpriteSheets();
    this.loadTileMap();
  }

  loadImages() {
    this.load.image("button1", "shared/images/ui/blue_button01.png");
    this.load.image("button2", "shared/images/ui/blue_button02.png");
    this.load.image("background", "shared/level/background-extruded.png");
  }

  loadSpriteSheets() {
    this.load.spritesheet("items", "shared/images/items.png", {
      frameWidth: 32,
      frameHeight: 32,
    });
    this.load.spritesheet("characters", "shared/images/characters.png", {
      frameWidth: 32,
      frameHeight: 32,
    });
    this.load.spritesheet("monsters", "shared/images/monsters.png", {
      frameWidth: 32,
      frameHeight: 32,
    });
  }

  loadAudio() {
    // Could provide a wav and mp3 and phaser will choose best one for broswser
    this.load.audio("goalSound", ["shared/audio/Pickup.wav"]);
    this.load.audio("enemyDeath", ["shared/audio/EnemyDeath.wav"]);
    this.load.audio("playerAttack", ["shared/audio/PlayerAttack.wav"]);
    this.load.audio("playerDamage", ["shared/audio/PlayerDamage.wav"]);
    this.load.audio("playerDeath", ["shared/audio/PlayerDeath.wav"]);
  }
  create() {
    this.scene.start("Title");
  }

  loadTileMap() {
    this.load.tilemapTiledJSON("map", "shared/level/large_level.json");
  }
}

export default BootScene;
