    if (this.player) {
      // emit player movement to the server
      const {
        x,
        y,
        flipX,
        playerAttacking,
        currentDirection
      } = this.player;

      if (this.player.oldPosition && (x !== this.player.oldPosition.x || y !== this.player.oldPosition.y || flipX !== this.player.oldPosition.flipX || playerAttacking !== this.player.oldPosition.playerAttacking)) {
        this.socket.emit('playerMovement', {
          x,
          y,
          flipX,
          playerAttacking,
          currentDirection
        });
      } // save old position data


      this.player.oldPosition = {
        x: this.player.x,
        y: this.player.y,
        flipX: this.player.flipX,
        playerAttacking: this.player.playerAttacking
      };
    }
