    this.goldPickupAudio = this.sound.add('goldSound', {
      loop: false,
      volume: 0.3
    });
    this.playerAttackAudio = this.sound.add('playerAttack', {
      loop: false,
      volume: 0.01
    });
    this.playerDamageAudio = this.sound.add('playerDamage', {
      loop: false,
      volume: 0.2
    });
    this.playerDeathAudio = this.sound.add('playerDeath', {
      loop: false,
      volume: 0.2
    });
    this.monsterDeathAudio = this.sound.add('enemyDeath', {
      loop: false,
      volume: 0.2
    });
  }

  createPlayer(playerObject, mainPlayer) {
    const newPlayerGameObject = new PlayerContainer(this, playerObject.x * 2, playerObject.y * 2, 'characters', 0, playerObject.health, playerObject.maxHealth, playerObject.id, this.playerAttackAudio, mainPlayer);

    if (!mainPlayer) {
      this.otherPlayers.add(newPlayerGameObject);
    } else {
      this.player = newPlayerGameObject;
    }
  }

  createGroups() {
    // create a chest group
    this.chests = this.physics.add.group(); // create a monster group

    this.monsters = this.physics.add.group();
    this.monsters.runChildUpdate = true; // create an other players group

    this.otherPlayers = this.physics.add.group();
    this.otherPlayers.runChildUpdate = true;
  }

  spawnChest(chestObject) {
    let chest = this.chests.getFirstDead();

    if (!chest) {
      chest = new Chest(this, chestObject.x * 2, chestObject.y * 2, 'items', 0, chestObject.gold, chestObject.id); // add chest to chests group

      this.chests.add(chest);
    } else {
      chest.coins = chestObject.gold;
      chest.id = chestObject.id;
      chest.setPosition(chestObject.x * 2, chestObject.y * 2);
      chest.makeActive();
    }
  }

  spawnMonster(monsterObj) {
    let monster = this.monsters.getFirstDead(); // Loop through chest group and get first inactive object in array
    // If none active - phase will return null

    if (!monster) {
      monster = new Monster(this, monsterObj.x, monsterObj.y, "monsters", monsterObj.frame, monsterObj.id, monsterObj.health, monsterObj.maxHealth);
      this.monsters.add(monster);
      monster.setCollideWorldBounds(true);
    } else {
      // monster.coins = monsterObj.gold;
      monster.id = monsterObj.id;
      monster.health = monsterObj.health;
      monster.maxHealth = monsterObj.maxHealth;
      monster.setTexture("monsters", monsterObj.frame);
      monster.setPosition(monsterObj.x, monsterObj.y);
      monster.makeActive();
    }
  }

  createInput() {
    this.cursors = this.input.keyboard.createCursorKeys();
  }

  addCollisions() {
    this.physics.add.collider(this.player, this.map.blockedLayer);
    this.physics.add.overlap(this.player, this.chests, this.collectChest, null, this);
    this.physics.add.collider(this.monsters, this.map.blockedLayer);
    this.physics.add.overlap(this.player.weapon, this.monsters, this.enemyOverlap, null, this);
    this.physics.add.collider(this.otherPlayers, this.player, this.pvpCollider, false, this); // Check overlaps between weapons and other playe game objects

    this.physics.add.overlap(this.player.weapon, this.otherPlayers, this.weaponOverlapEnemy, false, this);
  }

  pvpCollider(player, otherPlayer) {
    this.player.body.setVelocity(0);
    otherPlayer.body.setVelocity(0);
  }

  weaponOverlapEnemy(player, enemy) {
    if (this.player.playerAttacking && !this.player.swordHit) {
      this.player.swordHit = true;
      this.socket.emit("attackedPlayer", enemy.id);
    }
  }

  enemyOverlap(player, enemy) {
    if (this.player.playerAttacking && !this.player.swordHit) {
      this.player.swordHit = true; // enemy.makeInactive();

      this.socket.emit("monsterAttacked", enemy.id);
    }
  }

  collectChest(player, chest) {
    chest.makeInactive();
    this.goldPickupSound.play();
    this.socket.emit("updateScore", this.score); // Delayed call so the chest if it spawns in same location as player its not instantly collected
    // this.time.delayedCall(1000, this.spawnChest, [], this);

    this.socket.emit("pickupChest", chest.id);
  }

  createMap() {
    this.map = new Map(this, "map", "background", "background", "blocked");
  }

}

export default GameScene;

